import './Style.css';
import React, { useState, useRef, useEffect } from "react";
import heart from './gameheart.png'







const Main = () => {







    const [time, setTime] = useState(60)
    const intervalRef = useRef(null)
    const [lives, setLives] = useState(3)
    const [score, setScore] = useState(0)
    const [highScore, setHighScore] = useState(0)
    const [isGameOver, setIsGameOver] = useState(false)
    const [gameStarted, setGameStarted] = useState(false)
    const [randomArray, setRandomArray] = useState([])

    useEffect(() => {
        createRandomArray();
      }, []);

    const createRandomArray = () => {
        let orderedArray = []
        for (let i = 1;i <=100; i++){
            orderedArray.push(i)
        }
        console.log(orderedArray)
        let randomArray = []
        while (orderedArray.length > 0){
            let n = Math.floor(Math.random()*orderedArray.length)
            randomArray.push(orderedArray.splice(n, 1))
        }
        setRandomArray(randomArray.flat())
        console.log(randomArray)

    }


    const newGame = () =>{
        setGameStarted(true)
        setTime(60)
        if (intervalRef.current){
            clearInterval(intervalRef.current)
        }
        setLives(3)
        setScore(0)
        setIsGameOver(false)
        intervalRef.current = setInterval(reduceTime,1000)

    }

    const reduceTime = () => {
        setTime((prevTime) => (prevTime > 0 ? prevTime-1 : 0))
    }

    const newGameButton = () => {
        return(
            <div><button onClick={newGame} id="newgame">New Game</button></div>
        )
    }

    const livesDisplay = () => {

        const heartIcons = Array.from({ length: lives }, (_, index) => (
            <img src={heart} key={index} className="heartIcon" />
        ));
        return heartIcons;
        };


    const timerBar = () => 

    {
        const timerBarWidth = (time / 60 * 100) + "%"
            return(
            <div className="outerbox">
                <div className="innerbox" style={{width: timerBarWidth, backgroundColor: time > 30 ? "green" : time > 10 ? "#DAA520" : "red"}}>

                </div>
            </div>
        )
    }

    useEffect(() =>{
        if (lives === 0||time === 0){
            setIsGameOver(true)
            clearInterval(intervalRef.current)
        }
        }, [lives, time])   






    return (
        <div className="main">
            {gameStarted===false && newGameButton()}
            <div className = "condition">
                Condition goes here
            </div>
            <div className="grid">
            {randomArray.map((number) => (
          <button key={number} className="grid-square">
            {number}
          </button>
        ))}
            </div>
            <h3 style = {{
                color: time <= 5 ? "red" : ""
            }}>Time remaining: {time}</h3>
            <div className="timerContainer">{timerBar()}</div>
            <div className="livesContainer">
            {livesDisplay()}
            </div>

        </div>
    )
}

export default Main
