import './Style.css';
import React, { useState, useRef, useEffect } from "react";
import heart from './gameheart.png'







const Main = () => {

    const createRandomArray = () => {
        let orderedArray = []
        for (let i = 1;i <=100; i++){
            orderedArray.push(i)
        }
        console.log(orderedArray)
        let randomArray = []
        while (orderedArray.length > 0){
            let n = Math.floor(Math.random()*orderedArray.length)
            randomArray.push(orderedArray.splice(n, 1))
        }
        randomArray = randomArray.flat()
        console.log(randomArray)
        return randomArray.map((number => (
            <button className="grid-square">{number}</button>
        ))

        )
    }





    return (
        <div className="main">
            <div className = "condition">
                Condition goes here
            </div>
            <div className="grid">
                {createRandomArray()}
            </div>


        </div>
    )
}

export default Main
